<?php/***************************************************************************************Semplice script per fare un search dell'IP su varie Blocklist****************************************************************************************/function dnsbllookup($ip){	$listed = 0;	$dnsbl_lookup=array(		"eb.barracudacentral.org",		"bl.deadbeef.com",		"bl.emailbasura.org",		"bl.spamcannibal.org",		"bl.spamcop.net",		"blackholes.five-ten-sg.com",		"blacklist.woody.ch",		"bogons.cymru.com",		"cbl.abuseat.org",		"cdl.anti-spam.org.cn",		"combined.abuse.ch",		"combined.rbl.msrbl.net",		"db.wpbl.info",		"dnsbl-1.uceprotect.net",		"dnsbl-2.uceprotect.net",		"dnsbl-3.uceprotect.net",		"dnsbl.cyberlogic.net",		"dnsbl.inps.de",		"dnsbl.njabl.org",		"dnsbl.sorbs.net",		"drone.abuse.ch",		"duinv.aupads.org",		"dul.dnsbl.sorbs.net",		"dul.ru",		"dyna.spamrats.com",		"dynip.rothen.com",		"http.dnsbl.sorbs.net",		"images.rbl.msrbl.net",		"ips.backscatterer.org",		"ix.dnsbl.manitu.net",		"korea.services.net",		"misc.dnsbl.sorbs.net",		"multi.surbl.org",		"noptr.spamrats.com",		"orvedb.aupads.org",		"pbl.spamhaus.org",		"phishing.rbl.msrbl.net",		"proxy.bl.gweep.ca",		"psbl.surriel.com",		"rbl.interserver.net",		"recent.spam.dnsbl.sorbs.net",		"relays.bl.gweep.ca",		"relays.bl.kundenserver.de",		"relays.dnsbl.sorbs.net",		"relays.nether.net",		"sbl.spamhaus.org",		"short.rbl.jp",		"smtp.dnsbl.sorbs.net",		"socks.dnsbl.sorbs.net",		"spam.abuse.ch",		"spam.dnsbl.sorbs.net",		"spam.rbl.msrbl.net",		"spam.spamrats.com",		"spamlist.or.kr",		"spamrbl.imp.ch",		"tor.dnsbl.sectoor.de",		"torserver.tor.dnsbl.sectoor.de",		"ubl.lashback.com",		"ubl.unsubscore.com",		"virbl.bit.nl",		"virus.rbl.jp",		"virus.rbl.msrbl.net",		"web.dnsbl.sorbs.net",		"wormrbl.imp.ch",		"xbl.spamhaus.org",		"zen.spamhaus.org",		"zombie.dnsbl.sorbs.net",		"dnsbl.dronebl.org", 		"psbl.org","blacklist.myip.ms"	); // Add your preferred list of DNSBL's	if($ip){		$reverse_ip=implode(".",array_reverse(explode(".",$ip)));		foreach($dnsbl_lookup as $host){			if(checkdnsrr($reverse_ip.".".$host.".","A")){				$listed++;				}			}		}	return $listed;}?> 